CREATE TABLE xmppbot__bot_config ( custom_config_id integer not null, custom_name varchar(255) default '' not null, custom_value varchar(255) default '' not null, PRIMARY KEY (custom_config_id, custom_name));
CREATE TABLE xmppbot__command_config ( command_config_id integer not null, command_name varchar(255) default '' not null, command_option varchar(255) default '' not null, command_value varchar(255) default '' not null, PRIMARY KEY (command_config_id, command_name, command_option));
CREATE TABLE xmppbot__filter_config ( filter_config_id integer not null, filter_name varchar(255) default '' not null, filter_option varchar(255) default '' not null, filter_value varchar(255) default '' not null, PRIMARY KEY (filter_config_id, filter_name, filter_option));
CREATE TABLE xmppbot__logs_config ( logs_config_id integer not null, logs_name varchar(255) not null,  logs_type varchar(255) default '' not null, logs_fileformat varchar(255) default '' not null, logs_entryformat varchar(255) default '' not null, logs_enabled integer default 0 not null, logs_keepopen integer default 0 not null, PRIMARY KEY (logs_config_id, logs_name));
CREATE TABLE xmppbot__muc_config ( muc_config_id integer primary key not null, muc_name varchar(255) default '' not null,  muc_room varchar(255) default '' not null, muc_server varchar(255) default '' not null);
CREATE TABLE xmppbot__server_config ( server_config_id integer primary key not null, server_user varchar(255) default '' not null, server_password varchar(255) default '' not null, server_address varchar(255) default '' not null, server_resource varchar(255) default '' not null);
